import os

# create a bunch adaptive filters
adfilt = dict(
    nlms=dict(
        filter=pra.adaptive.NLMS(length, mu=0.5),
        error=np.zeros(n_samples),
    ),
    blocklms=dict(
        filter=pra.adaptive.BlockLMS(length, mu=0.033, L=8, nlms=True),
        error=np.zeros(n_samples),
    ),
    rls=dict(
        filter=pra.adaptive.RLS(length, lmbd=1.0, delta=2.0),
        error=np.zeros(n_samples),
    ),
    blockrls=dict(
        filter=pra.adaptive.BlockRLS(length, lmbd=1.0, delta=2.0, L=8),
        error=np.zeros(n_samples),
    ),
)

for i in range(n_samples):
    for algo in adfilt.values():
        algo["filter"].update(x[i], d[i])
        algo["error"][i] = np.linalg.norm(algo["filter"].w - w)

plots_folder = './graphs'

plt.figure(figsize=(16, 10), dpi=80)
plt.rc('font', size=20)          # controls default text sizes
plt.rc('xtick', labelsize=20)    # fontsize of the tick labels
plt.rc('ytick', labelsize=20)
plt.figure()

for algo in adfilt.values():
    plt.semilogy(algo["error"])
plt.legend(adfilt)
plt.title("Convergence to W filter")
plt.tight_layout()
plt.savefig(os.path.join(plots_folder, 'algorithms_convergence.jpg'))
plt.clf()